 const Reducer = (state,action) => {

    switch (action.type) {
        case 'removeitem':{
            return {...state, item : state.item.filter((elem)=> {
                return elem.id !== action.payload
            } )}
        }
            
       
    
        default:
    }

   return  state;
   
 }
 
 export default Reducer
