const Reducer = (state, action) => {

    if (action.type === 'REMOVE') {
        return {
            ...state,
            item: state.item.filter((elem) => elem.id !== action.payload)
        }
    }

    if (action.type === 'REMOVEALL') {
        return { ...state, item: [] }
    }

    if (action.type === 'INCREASE') {
        const updateincrease = state.item.map((elem) => {
            if (elem.id === action.payload) {
                return { ...elem,
                     quantity: elem.quantity + 1 ,
                     price : elem.price + elem.price
                    };
            }
            return elem
        })
        return { ...state, item: updateincrease }
    }

    if (action.type === 'DECREASE') {
        const updatedecrease = state.item.map((elem) => {
            if (elem.id === action.payload) {
                return { ...elem, quantity: elem.quantity - 1};
                //  if (elem.quantity>1) {
                //     return {...elem,quantity:elem.quantity-1};
                //  }
            }
            return elem
        }).filter((elem) => elem.quantity !== 0)
        return { ...state, item: updatedecrease }
    }

    if (action.type === 'total_item') {
        let { totalItem } = state.item.reduce((accum, curVal) => {
            let { quantity } = curVal;
            accum.totalItem += quantity;
            return accum;
        }, {
            totalItem: 0
        })
        return { ...state, totalItem }
    }

    if (action.type === 'total_amount') {
        let { totalAmount } = state.item.reduce((acum, val) => {
            let { price } = val;
            acum.totalAmount += price;
            return acum;
        }
            , {
                totalAmount: 0
            })
            return {...state,totalAmount}
    }



    return state;

}

export default Reducer
